import React, { useContext } from 'react';
import { MenuContext } from './MenuContext'
import '../../stylesheets/WaiterView/Plates.css';
import ButtonsAddLess from './ButtonsAddLess'

 const Plates = (props) => {
  const { addItemToCart } = useContext(MenuContext)
  return (
   
    <div className="show_menues">
    <div className="container_products">
    <button className='btn_plates'><img className="img_product" src= {props.img}/>
    <br/>{props.name}
    <br/>${props.price}
    </button>
    </div>
    <ButtonsAddLess/>
    </div>

   )
   
}
export default Plates;
