import TableHeader from './TableHeader';
import '../../stylesheets/WaiterView/TableMain.css';

const CartItem = ({data,delFromCart}) => {
    let {id,name,price, quantity} = data;
    const subtotal = price*quantity;
    return (
        <div className='div_select_item_by_customer'>
        <table id="table_select_item" className="table table-striped"></table>
        <TableHeader/>
         {/* <thead>
    <tr>
    <th className="th_item">Item</th>
    <th className="th_quantity">Cantidad</th>
    <th className="th_price">Precio</th>
    <th className="num"></th>
    </tr>
    </thead> */}

   
  <td><th>{name}</th></td>
  <td><th>{quantity}</th></td>
  <td><th>${subtotal} = </th></td>
  <button onClick={() => delFromCart(id)}>ELIMINAR UNO</button>
  <button onClick={() => delFromCart(id, true)}>ELIMINAR Todo</button>

       
            </div>
          
    )
}
export default CartItem;
