import '../../stylesheets/WaiterView/Plates.css';
const ProductItem = ({data, addToCart}) => {
    let { id,name,img, price } = data;

    return (
        <div className="show_menues">
        <div className="container_products">
        <button className='btn_plates'><img className="img_product" src ={ img} /><br/>
        { name } <br/> $ { price }  </button>
     {/* /<div style={{border:"thin solid white", padding: "2px", color: "white"}}> */}
    {/* <h4>{name}</h4>
    <h5>${price}</h5> */}
    <button className='btn_add' onClick={() => addToCart(id)}>Agregar</button>
    </div>
     </div>

    )
}

export default ProductItem;
