import '../../stylesheets/WaiterView/Plates.css';
const Plates = ({data, addToOrder}) => {
    let { id,name,img, price } = data;

    return (
        <div className="show_menues">
        <div className="container_products">
        <button className='btn_plates'>
        <img className="img_product" src ={ img} /><br/>
        { name } <br/> $ { price }  
        </button>
        <button className='btn_add' onClick={() => addToOrder(id)}>
        Agregar<i className="icon-plus-sign"></i>
        </button>
        </div>
        </div>
    )
};

export default Plates;
