import React, { useState } from 'react';
import { useAuth } from '../context/authContext'
import { useNavigate } from 'react-router-dom';

import 'bootstrap/dist/css/bootstrap.min.css';

export function Login() {

  const [user, setUser] = useState({
    email: "",
    password: "",
  });

  // Exportando login
  const {login} = useAuth();
  const navigate = useNavigate();
  const [error, setError] = useState('');

  // Actualizar estado
  const handleChange = ({ target: { name, value } }) =>
  setUser({...user, [name]: value});

  const handleSubmit = async(e) => {
    e.preventDefault();
    setError('');
    try{
      await login(user.email, user.password);
      navigate('/Food');
    } catch (error) {
      setError(alert(error.message));
    }
 };


 return (
//  <div>{error && <p>{error}</p>}


 <div className="container-login">
 <div className="row">
  <div className="col">
  <div class="shadow-lg p-3 mb-5 mt-4 bg-body rounded">
  <div class="p-3 mb-2 bg-primary bg-gradient fw-bold text-white">Login</div>
    <form class="row g-3 needs-validation" onSubmit={handleSubmit} novalidate>
    <div className="form-login">
    <div className="col-md-4">
    <label htmlFor="email" className="form-label">Correo electr칩nico</label>
    <input type="email" name="email" className="form-control" id="email" aria-describedby="emailHelp" placeholder="Correo electr칩nico" onChange={handleChange}/>
    <div class="valid-feedback">Campo ingresado</div>
    <div class="invalid-feedback">Debes completar este campo</div>
    </div>

    <div className="col-md-4">
    <label htmlFor="password" className="form-label">Contrase침a</label>
    <input type="password" name="password" className="form-control" id="password" placeholder="Contrase침a" onChange={handleChange} />
    <div class="valid-feedback">Campo ingresado</div>
    <div class="invalid-feedback">Debes completar este campo</div>
    </div>

    <button type="submit" className="btn btn-primary" id="btn-submit">Ingresar</button>

     {/* <div className= "alert alert-danger d-flex align-items-center" role="alert">
    {error && <p>{error}</p>}
    </div> */}
    </div>
    </form>
    </div>
</div>
</div>
    </div>
    )}
