import React, { useState } from 'react';
import Header from '../WaiterView/Header';
import Inputs from '../WaiterView/Inputs';
import Plates from './Plates';
import TableHeader from '../WaiterView/TableHeader';
import menu from '../../../src/data/menu.json'
import '../../stylesheets/WaiterView/WaiterMainView.css';


const Main = () => {
    const [cartItems, setPlates] = useState(menu)
    console.log(setPlates)

    const addItemToCart = (product) => {
        const inCart = cartItems.find(
         (productInCart) => productInCart.id === product.id
         );
       
       if(inCart) {
         setPlates(
           cartItems.map((productInCart) => {
             if (productInCart.id === product.id) {
               return { ...inCart, amount: inCart.amount + 1 }
             } else return productInCart;
            })
         )
            } else {
               setPlates([...cartItems, { ...product, amount:1}])
             }
       }
       const deleteItemToCart = (product) => {
         const inCart = cartItems.find(
           (productInCart) => productInCart.id === product.id
           );
     
       if(inCart.amount === 1) {
         setPlates(
           cartItems.filter((productInCart) => productInCart.id !== product.id) 
         );
       }else {
         setPlates(
           cartItems.map((productInCart) => {
             if (productInCart.id === product.id) {
               return {...inCart, amount: inCart.amount - 1}
               } else return productInCart
             })
         )
       }
     };
     
    return (
        <div>
        <Header/>
        <Inputs/>
        <div className='container-plates'> 
        {cartItems.map((plate) => 
        <Plates
        key={plate.id}
        // img={plate.img}
        // name={plate.name}
        // price={plate.price}
        {...plate}
        />
        )}
        </div>
        <div className='div_select_item_by_customer'>
        <table id="table_select_item" className="table table-striped">
        <TableHeader/>
        </table>

        <button onClick={addItemToCart}>agregar</button>
        <button onClick={deleteItemToCart}>eliminar</button>
        </div>
        </div>
        
        
    )
}
export default Main;
