import React, { useState } from 'react';
import { useAuth } from '../context/authContext'
import { useNavigate } from 'react-router-dom';

import 'bootstrap/dist/css/bootstrap.min.css';

export function Login() {

  const [user, setUser] = useState({
    email: "",
    password: "",
  });

  // Exportando login
  const {login} = useAuth();
  const navigate = useNavigate();
  const [error, setError] = useState('');

  // Actualizar estado
  const handleChange = ({ target: { name, value } }) =>
  setUser({...user, [name]: value});

  const handleSubmit = async(e) => {
    e.preventDefault();
    setError('');
    try{
      await login(user.email, user.password);
      navigate('/Food');
    } catch (error) {
      setError(alert(error.message));
    }
 };


 return (
//  <div>{error && <p>{error}</p>}


 <div className='container-login'>
  <div className='col'>
  <div className='row'>
    <form className="row g-3" onSubmit={handleSubmit} novalidate>
    <div className='form-login'>
    <div className="mb-3">
    <label htmlFor="validationServer01" className="form-label">Correo electr칩nico</label>
    <input type="email" name="email" className="form-control" id="email" aria-describedby="emailHelp" placeholder="Correo electr칩nico" onChange={handleChange} required/>
    <div class="invalid-feedback">
      Looks good!
    </div>
    </div>

    <div className="mb-3">
    <label htmlFor="exampleInputPassword1" className="form-label">Contrase침a</label>
    <input type="password" name="password" className="form-control" id="password" placeholder="Contrase침a" onChange={handleChange} required />
    </div>

    <button type="submit" className="btn btn-primary" id="btn-submit">Ingresar</button>

     {/* <div className= "alert alert-danger d-flex align-items-center" role="alert">
    {error && <p>{error}</p>}
    </div> */}
    </div>
    </form>
    </div>
</div>
    </div>
    )}
