import React, { useState } from 'react';

export function ButtonsAddLess ()  {
    const [counterState, setCounterState] = useState(0);
   

    const handleIncrement = () => {
       console.log('AAAAAAAAAAAAAAAAAAAA')
        setCounterState(counterState + 1);
    } 

    const handleDecrement = () => {
        console.log('BBBBBBBBBBBBBBB')
        setCounterState(counterState - 1);
    } 

    return(
    <div className="div_add_subs1">
    <button className='btn_add' onClick = {handleIncrement}><i className="icon-plus"></i></button>
    <span className="span_quantity">{counterState}</span>
    <button className='btn_subs' onClick = {handleDecrement} ><i className="icon"></i></button>
    </div>
)

}
