import React from 'react';
import '../../stylesheets/WaiterView/Plates.css';
import Inputs from '../WaiterView/Inputs';
import Header from '../WaiterView/Header';

 const Plates = (props) => {

  return (
    <div>
    <Header/>

    <Inputs/>
    <div className="show_menues">
    <div className="container_products">
    <button className='btn_plates'><img className="img_product" src= {props.img}/>
    <h3 style={{color:"white"}}>{props.name}</h3>
    <h3 style={{color:"white"}}>${props.price}</h3>
    </button>
    </div>
    </div>
    </div>
   )
   
}
export default Plates;
