import React from 'react';
import '../../stylesheets/WaiterView/Plates.css';

 const Plates = (props) => {

  return (
    <div className="show_menues">
      <div className="container_products">
        <button className='btn_plates'>
        <img className="img_product" src= {props.img}/>
    <h2 style={{color:"white"}}>{props.name}</h2>
    <h2 style={{color:"white"}}>${props.price}</h2>
</button>
    </div>
</div>
   )
}
export default Plates;
