import Plates from './Plates';
import React from 'react';

const Cart = ({cart, setCart}) => {
  return (
    <div className='cart'>
      <h1>Carrito</h1>
      {cart.length === 0 ? (
        <p>0</p>
        ):(
          cart.map((plate) => (
      <Plates
      key = {plate.id}
      plate= {plate}
      cart = {cart}
      setCart = {setCart}
      />
      ))
      )}
    </div>
  );
};
export default Cart
