import React, { useState } from 'react';
import menu from '../../../src/data/menu.json'
import '../../stylesheets/WaiterView/WaiterMainView.css';

const Main = () => {
     const [plates, setPlates] = useState(menu)
    console.log(plates)
    return (
        <div>
        <Header/>
    
        <Inputs/>
        <div className='container-plates'> 
        {plates.map((plate) => 
       
        <Plates
        img={plate.img}
        key={plate.id}
        name={plate.name}
        price={plate.price}
/>
        )}
        </div>
        <ButtonsAddLess/>
</div>
    )
}
export default Main;
