/* eslint-disable no-unused-vars */
import React from 'react';
import { useNavigate } from 'react-router-dom';
import Menu from '../data/menu.json';
import '../../src/stylesheets/ButtonsOptionsFood.css'

export function Food({ menu }) {
    const navigate = useNavigate()
    let showBreakfast = [];
    let showDishes = [];
    let showDrinks = [];
    showBreakfast = Menu && Menu.filter((category =>  category.categoria === "breakfast"))

        showMenu = Menu && Menu.filter((category =>  category.categoria === "dishes"))

        showMenu = Menu && Menu.filter((category =>  category.categoria === "drinks"))


  

    return (
        <div className='container-options'>
            <div className='btn-options'>
            <button className='btn-food' onClick={function(){navigate('/breakfast')}}><img src={require('../img/image-breakfast.jpg')} className='img-btn'/> 
            <p className='p-food'>DESAYUNO</p>
            </button>



            <button className='btn-food' onClick={function(){navigate('/lunch')}}><img src={require('../img/image-lunch.jpg')} className='img-btn'/> 
            <p className='p-food'>ALMUERZO</p>
            </button>

            <button className='btn-food' onClick={function(){navigate('/drinks')}}><img src={require('../img/image-drinks.jpg')} className='img-btn'/> 
            <p className='p-food'>BEBIDAS</p>
            </button>
            </div>


        </div>
    )
}
