import '../../stylesheets/WaiterView/TableMain.css';

const CartItem = ({data,delFromCart}) => {
    let {id,name,price, quantity} = data;
    return (
        <tbody>
        <tr>
    {/* <div> */}
    <th><h3>{name}</h3></th>
            <h5>${price} x {quantity} = {price*quantity}</h5>
            <button onClick={() => delFromCart(id)}><i className="icon-minus"></i>ELIMINAR UNO</button>
            <button onClick={() => delFromCart(id, true)}><i className="icon-trash"></i>ELIMINAR Todo</button>
            </tr>
            {/* // </div> */}
            </tbody>
    )
}
export default CartItem;
