import { Inputs } from './Inputs';
import 'bootstrap/dist/css/bootstrap.min.css';
import '../../src/stylesheets/Plates.css';
//import Menu from '../data/menu.json';


const Plates = ({plate, cart, setCart, plates}) => {
  const { id, nombre, precio} = plate;

  const addPlate = (id) => {
    const plate = plates.filter((plate) => plate.id === id)
    setCart([...cart, ...plate])
  };

  const deletePlate = (id) => {
    const plates = cart.filter(plate => plate.id !== id)
    setCart(plates)
  }
    return (
    <div className='container-plates'>
    <ul>
      <li>{nombre} </li>
      <li>{precio} </li>
        </ul>
        {plates ? (
          <button type="button" onClick= {() => addPlate(id)}>
          agregar al carrito
