/* eslint-disable no-unused-vars */
import React from 'react';
import { useNavigate } from 'react-router-dom';
import Menu from '../data/menu.json';
import { ButtonsAddLess } from './ButtonsAddLess';
import '../../src/stylesheets/ButtonsOptionsFood.css'

export function Food({ menu }) {
    const navigate = useNavigate()
    let showBreakfast = [];
    let showDishes = [];
    let showDrinks = [];
    showBreakfast = Menu && Menu.filter((item =>  item.categoria === "breakfast")).map(product => {
        return (
          <div className="container_products" key={product.id}>
          <button className='btn_breakfast'><img className="img_product" src ={ product.img} /><br/>{ product.nombre } <br/> $ { product.precio }  </button>
          <ButtonsAddLess/>
          </div>
        )
      })
    
    showDishes = Menu && Menu.filter((item =>  item.categoria === "dishes")).map(product => {
        return (
          <div className="container_products" key={product.id}>
          <button className='btn_breakfast'><img className="img_product" src ={ product.img} /><br/>{ product.nombre } <br/> $ { product.precio }  </button>
          <ButtonsAddLess/>
          </div>
        )
      })
    showDrinks = Menu && Menu.filter((item =>  item.categoria === "drinks")).map(product => {
        return (
          <div className="container_products" key={product.id}>
          <button className='btn_breakfast'><img className="img_product" src ={ product.img} /><br/>{ product.nombre } <br/> $ { product.precio }  </button>
          <ButtonsAddLess/>
          </div>
        )
      })


    return (
        <div className='container-options'>
            <div className='btn-options'>
            <button className='btn-food' onClick={function(){navigate(showBreakfast)}}><img src={require('../img/image-breakfast.jpg')} className='img-btn'/> 
            <p className='p-food'>DESAYUNO</p>
            </button>



            <button className='btn-food' onClick={function(){navigate(showDishes)}}><img src={require('../img/image-lunch.jpg')} className='img-btn'/> 
            <p className='p-food'>ALMUERZO</p>
            </button>

            <button className='btn-food' onClick={function(){navigate(showDrinks)}}><img src={require('../img/image-drinks.jpg')} className='img-btn'/> 
            <p className='p-food'>BEBIDAS</p>
            </button>
            </div>


        </div>
    )
}
