import React, { useEffect, useState } from 'react';
import plates from '../../../src/data/menu.json'

export default function Plates() {
    const [product, setProduct] = useState(plates)
const showMenu = () => { 
    return setProduct(product)
}
  return (
    <>
    {showMenu.map(plate => (
      <>
<h1 key={plate.id}></h1>
<h2>{plate.name}</h2>
<h2>{plate.price}</h2>
<img src = {plate.img} />
</>
    ))}
    </>
  )
    
    }
