import React from 'react';
import { useCart } from 'react-use-cart';
import { addOrder } from '../../firebase/orders';
import data from './data';

const Cart = () => {
    const {
        isEmpty,
        totalUniqueItems,
        items,
        totalItems,
        cartTotal,
        updateItemQuantity,
        removeItem,
        emptyCart,
    } = useCart();
    if (isEmpty) return <h1 className='text-center'> You cart is Empty</h1>

    const enviarOrden = () => {
        console.log(data.productData, "aaaaaaaaaaaaaa")
        addOrder(data.productData)
        
    }

    return(
        <section className='py-4 container'>
        <div className='row justify-content-center'>
        <div className='col-12'>
            <h5>Cart ({totalUniqueItems}) total Items: ({totalItems})</h5>
            <table className='table table-light table-hover m-0'>
            <tbody>
            {items.map((item, index) => {
                return(
                    <tr key = {index}>
                    <td>
                        <img src={item.img} style={{height: '6rem'}} />
                    </td>
                    <td>{item.name}</td>
                    <td>{item.price}</td>
                    <td>Quantity ({item.quantity})</td>
                    <td>
                        <button className="btn btn-info ms-2" onClick={() => updateItemQuantity(item.id, item.quantity - 1)}>-</button>
                        <button className="btn btn-info ms-2" onClick={() => updateItemQuantity(item.id, item.quantity + 1)}>+</button>
                        <button className="btn btn-info ms-2" onClick={() => removeItem(item.id)}>Remove Item</button>
                    </td>
                </tr>
                )
            })}
            </tbody>
            </table>
        </div>
        <div className="col-auto ms-auto">
        <h2>Total Price: ${cartTotal}</h2>
        </div>
        <div className="col-auto">
        <button
        className="btn btn-danger m-2" onClick = {() => emptyCart()}
        >Clear Cart</button>
        <button
        className="btn btn-primary m-2" onClick = {() => enviarOrden()}
        >Buy Now</button>
        </div>
        </div>
        </section>
    );
};
export default Cart;
