import React, { useState } from 'react';
import { MenuContext } from '../../Context/MenuContext'
import Header from '../WaiterView/Header';
import Inputs from '../WaiterView/Inputs';
import Plates from './Plates';
import TableHeader from '../WaiterView/TableHeader';
import menu from '../../../src/data/menu.json'
import '../../stylesheets/WaiterView/WaiterMainView.css';


const Main = () => {
    const [plates, setPlates] = useState(menu)
    const { addItemToCart } = useContext(MenuContext)
    console.log(setPlates)
    return (
        <div>
        <Header/>
        <Inputs/>
        <div className='container-plates'> 
        {plates.map((plate) => 
        <Plates
        key={plate.id}
        // img={plate.img}
        // name={plate.name}
        // price={plate.price}
        {...plate}
        />
        )}
        </div>
        <div className='div_select_item_by_customer'>
        <table id="table_select_item" className="table table-striped">
        <TableHeader/>
        </table>
        </div>
        </div>
        
        
    )
}
export default Main;
