import MenuButtons from '../MenuButtons'
import SidebarLunch from './SidebarLunch'
import Header from "./Header";
import { Link } from 'react-router-dom'
import menu from '../../data/menu.json';
import { useState } from 'react'
import '../../stylesheets/WaiterView/Plates.css';

export const Lunch = () => {

    const [cartItems, setCartItems] = useState([]);
    const onAdd = (item) => {
        const exist = cartItems.find((x) => x.id === item.id)
        if(exist) {
          setCartItems(cartItems.map((x) => x.id === item.id ? {...exist, qty: exist.qty + 1} : x)         );
        } else {
          setCartItems([...cartItems, {...item, qty: 1}])
        }
      };
    
      const onRemove = (item) => {
         const exist = cartItems.find((x) => x.id === item.id);
         if(exist.qty === 1) {
           setCartItems(cartItems.filter((x) => x.id !== item.id));
         } else {
          setCartItems(cartItems.map((x) => 
          x.id === item.id ? {...exist, qty: exist.qty - 1} : x
            )
          );
         }
      }
    
    
  return (
    <>
       
                <Header />
            <MenuButtons />
            <div className="show_menues">
        <div className="container_products">
          {menu.lunch.map((item) => {
            return (
              <button className='btn_plates' key={item.id} onClick={() => onAdd(item)}>
              <img className="img_product" src ={ item.img} /><br/>{ item.name }<br/> $ { item.price }
              </button> 
            )
        })}
        </div>
        <Link to="/" className='back'>Return</Link>
        <Link to="/orders" className="ordersBtn">
          Orders
        </Link>
        
        
        <div className='sideBar'>
            <SidebarLunch
            onAdd={onAdd}
            onRemove={onRemove}
            cartItems={cartItems} />
        </div>
    </div>
    </>
  )
}
