    //   <Route path="/plates" element={<Plates/>}/>
    // </Routes>
    // </AuthProvider>

    <Fragment>
      {plates.map((plate) => (
        <Plates
        key = { plate.id }
        plate={plate}
        cart={cart}
        setCart= { setCart}
        plates= { plates }
        />
            
      ))


      }
    
      </Fragment>
//    </div>
    // </div>
  );
}

export default App;
