import React, { useState, useContext } from 'react';
import { logout } from '../../context/authContext';
import { auth } from '../../firebase/config';
import { useNavigate, useLocation } from 'react-router-dom';
import { useAuth, authContext  } from '../../context/authContext';
import Swal from "sweetalert2";
import styles from './Header.module.css';
import 'bootstrap/dist/css/bootstrap.min.css';


const logo = require('../../img/font1.png')

function Header()  {
    const { logout } = useAuth();
    const navigate = useNavigate();
    const [error, setError] = useState('');
    const contextValue = useContext(authContext);
    let location = useLocation();
    const position = (auth.currentUser.email).indexOf('@');
    let username = (auth.currentUser.email).substring(0,position);

    const signOut = () => {
        logout()
        .then(() => 
        { {setTimeout(function() { navigate('/login')},1200)};
        sessionStorage.clear();})
        Swal.fire({
          
            title: '<b>Cerrando sesión</b>',
            position: 'bottom-end',
            icon: 'success',
            timer: 1500,
            timerProgressBar: true,
            showConfirmButton:false,
            allowOutsideClick: false,
            toast: true,
            backdrop: true,
            footer: 'Espere unos segundos por favor...',
          })
    }
    return (
    <div id={styles.divBtnHeader}>
    <img className= {styles.imgLogo} src={logo}/>
    <h3 className={styles.title}>TOMA DE PEDIDOS  {username}</h3>
    <button id={styles.btnLogout} className='btn btn-danger' onClick={signOut}><i id={styles.icoOff} className='icon-off'></i> Cerrar sesión</button>
    </div>
    )
};
export default Header;
