import SidebarLunch from './PriceLunch'
import Header from "./Header";
import Inputs  from './Inputs';
import { Link } from 'react-router-dom'
import menu from '../../data/menu.json';
import { useState } from 'react'
import styles from '../../stylesheets/WaiterView/Plates.module.css';

export const Plates = () => {

    const [cartItems, setCartItems] = useState([]);
    const addItem = (item) => {
        const exist = cartItems.find((x) => x.id === item.id)
        if(exist) {
          setCartItems(cartItems.map((x) => x.id === item.id ? {...exist, qty: exist.qty + 1} : x)         );
        } else {
          setCartItems([...cartItems, {...item, qty: 1}])
        }
      };
    
      const removeItem = (item) => {
         const exist = cartItems.find((x) => x.id === item.id);
         if(exist.qty === 1) {
           setCartItems(cartItems.filter((x) => x.id !== item.id));
         } else {
          setCartItems(cartItems.map((x) => 
          x.id === item.id ? {...exist, qty: exist.qty - 1} : x
            )
          );
         }
      }
  return (
    <>
       <Header />
       <Inputs />
       <div className={styles['container-plates']}> 
     
       
       {menu.lunch.map((item) => {
        return (
          <div className={styles['show_menues']}>
          <div className={styles['container_products']}>
          <button className={styles['btn_plates']} key={item.id} onClick={() => addItem(item)}>
          <img className="img_product" src ={ item.img} /><br/>{ item.name }<br/> $ { item.price }
          </button> 
          </div>
          </div>
          )
        })}
        
       
        </div>
        {/* <Link to="/" className='back'>Return</Link>
        <Link to="/orders" className="ordersBtn">
          Orders
        </Link> */}
        
        
        <div className='sideBar'>
            <SidebarLunch
            addItem={addItem}
            removeItem={removeItem}
            cartItems={cartItems} />
        </div>
  
    
    </>
  )
}
