import MenuButtons from '../MenuButtons.js'
import SidebarLunch from '../SidebarLunch.js'
import Header from "./Header";
import { Link } from 'react-router-dom'
import menu from '../../data/menu.json';
import { useState } from 'react'
import '../../stylesheets/WaiterView/Plates.css';

export default function WaiterLunch() {

    const [cartItems, setCartItems] = useState([]);
    const onAdd = (item) => {
        const exist = cartItems.find((x) => x.id === item.id)
        if(exist) {
          setCartItems(cartItems.map((x) => 
          x.id === item.id ? {...exist, qty: exist.qty + 1} : x
            )
          );
        } else {
          setCartItems([...cartItems, {...item, qty: 1}])
        }
      };
    
      const onRemove = (item) => {
         const exist = cartItems.find((x) => x.id === item.id);
         if(exist.qty === 1) {
           setCartItems(cartItems.filter((x) => x.id !== item.id));
         } else {
          setCartItems(cartItems.map((x) => 
          x.id === item.id ? {...exist, qty: exist.qty - 1} : x
            )
          );
         }
      }
    
    
  return (
    <div>
       
                <Header />
            <MenuButtons />
            <div className="show_menues">
        <div className="container_products">
        { Menu.lunch.map(item => {
            return (                
                    <button type='button'  
                    key={item.id} className='btn' 
                    onClick={() => onAdd(item)}>
                    {item.name} <br/> $ {item.price}.00
                    </button> 
            )
        })}
        </div>
        <Link to="/" className='back'>Return</Link>
        <Link to="/orders" className="ordersBtn">
          Orders
        </Link>
        
        
        <div className='sideBar'>
            <SidebarLunch
            onAdd={onAdd}
            onRemove={onRemove}
            cartItems={cartItems} />
        </div>
    </div>
    </div>
  )
}
