import MenuButtons from "../MenuButtons.js";
import SidebarBreakfast from "../SidebarBreakfast.js";
import Header from './Header';
import { Link } from "react-router-dom";
import menu from '../../data/menu.json';
import { useState } from "react";
import '../../stylesheets/WaiterView/Plates.css';

const WaiterBreakfast = () => {
  // const { itemsBreakfast } = Menu.breakfast;
  const [cartItems, setCartItems] = useState([]);
  const onAdd = (item) => {
    const exist = cartItems.find((item) => item.id === item.id);
    if (exist) {
      setCartItems(
        cartItems.map((x) =>
          x.id === item.id ? { ...exist, quantity: exist.quantity + 1 } : x
        )
      );
    } else {
      setCartItems([...cartItems, { ...item, quantity: 1 }]);
    }
  };

  const onRemove = (item) => {
    const exist = cartItems.find((x) => x.id === item.id);
    if (exist.quantity === 1) {
      setCartItems(cartItems.filter((x) => x.id !== item.id));
    } else {
      setCartItems(
        cartItems.map((x) =>
          x.id === item.id ? { ...exist, quantity: exist.quantity - 1 } : x
        )
      );
    }
  };

  return (
    <div>
          <Header />
          <MenuButtons />
    
        <div className="show_menues">
        <div className="container_products">
          {menu.breakfast.map((item) => {
            return (
              <button className='btn_plates' key={item.id} onClick={() => onAdd(item)}>
              <img className="img_product" src ={ item.img} /><br/>{ item.name }<br/> $ { item.price }
              </button>
            );
          })}
        </div>
        <Link to="/" className="back">
          Return
        </Link>
        <Link to="/orders" className="ordersBtn">
          Orders
        </Link>
      </div>
      <div className="sideBar">
        <SidebarBreakfast
          onAdd={onAdd}
          onRemove={onRemove}
          cartItems={cartItems}
        />
      </div>
    </div>
  );
};

export default WaiterBreakfast;
