/* eslint-disable no-unused-vars */
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Menu from '../data/menu.json';
import { ButtonsAddLess } from './ButtonsAddLess';
import '../../src/stylesheets/ButtonsOptionsFood.css'

export function Food() {
    const navigate = useNavigate()
    let menuItems =[];
    if (menu ==='breakfast') {
      menuItems = Menu.filter(menuItem =>  menuItem.categoria === "breakfast")
    }else{
      menuItems = Menu.filter(menuItem =>  menuItem.categoria === "dishes" || menuItem.categoria === "drinks" )
    }

    return (
        <div className='container-options'>
            <div className='btn-options'>
            <button className='btn-food' onClick={showBreakfast}><img src={require('../img/image-breakfast.jpg')} className='img-btn'/> 
            <p className='p-food'>DESAYUNO</p>
            </button>



            <button className='btn-food' onClick={showDishes}><img src={require('../img/image-lunch.jpg')} className='img-btn'/> 
            <p className='p-food'>ALMUERZO</p>
            </button>

            <button className='btn-food' onClick={showDrinks}><img src={require('../img/image-drinks.jpg')} className='img-btn'/> 
            <p className='p-food'>BEBIDAS</p>
            </button>
            </div>

            <div className="show_menues">
      {menuItems.map(product => {
          return (
            <div className="container_products" key={product.id}>
            <button className='btn_breakfast'><img className="img_product" src ={ product.img} /><br/>{ product.nombre } <br/> $ { product.precio }  </button>
            
            <div className="div_add_subs">
            <button className='btn_add' onclick={AddQuantity()}><i className="icon-plus-sign"></i></button>
            <span className="span_quantity">00</span>
            <button className='btn_subs'><i className="icon-minus"></i></button>
            </div>
            </div>
          )
        })

      }

    </div>
        </div>
        
    )
}
