import React, { useState } from 'react';
import { auth, logout } from '../../context/authContext';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../../context/authContext'
import styles from './Header.module.css';
import 'bootstrap/dist/css/bootstrap.min.css';


const logo = require('../../img/font1.png')

function Header()  {
    const { logout } = useAuth();
    const navigate = useNavigate();
    const [error, setError] = useState('');
  
    const signOut = () => {
        logout()
        .then(() => {navigate('/login');
        sessionStorage.clear();})
        .catch((error) => {alert(error)})
    }
    
    return (
    <div id={styles.divBtnHeader}>
    <img className= {styles.imgLogo} src={logo}/>
    <h3 className={styles.title}>TOMA DE PEDIDOS</h3>
    <button id={styles.btnLogout} className='btn btn-danger' onClick={signOut}><i id={styles.icoOff} className='icon-off'></i> Cerrar sesi√≥n</button>
    </div>
    )
};
export default Header;
