export function ButtonsAddLess ()  {
    const [counterState, setCounterState] = [useState(0)];
   

    const handleIncrement = () => {
       console.log('AAAAAAAAAAAAAAAAAAAA')
        setCounterState(counterState + 1);
    } 

    const handleDecrement = () => {
        
        setCounterState(counterState - 1);
    } 

    return(
    <div className="div_add_subs1">
    <button className='btn_add1' onClick = {handleIncrement}><i className="icon-plus-sign"></i></button>
    <span className="span_quantity1">00</span>
    <button className='btn_subs1' onClick = {handleDecrement} ><i className="icon-minus"></i></button>
    </div>
)

}
