        return setProducts(products)
    }

  return (
    <div className="show_menues">
    {products.map((product) => {
    return <div key={product.id}>
    <button onClick={showMenu}>aaaaaa</button>
    <h2 style={{color:"white"}}>${product.name}</h2>
    </div>
    
})}
</div>
   )
}
