import * as React from 'react';
import { createContext, useContext } from 'react';
import { signInWithEmailAndPassword, onAuthStateChanged, signOut } from 'firebase/auth';
import { auth } from '../firebase/config';
// Archivo que permite saber a las otras páginas que el usuario se ha logueado

export const authContext = createContext();

export const useAuth = () => useContext(authContext)


export function AuthProvider({ children }) {

  const login = (email, password) =>  signInWithEmailAndPassword(auth, email, password);
  const logout = () =>  signOut(auth) ;

  useEffect(() => {
    const unsubcribe = onAuthStateChanged(auth, (currentUser) => {
      setUser({ currentUser: currentUser?.uid });
      //* We put a boolean on it when the user is already authenticated, it change to false so that it is no longer displayed but remember that the tmb role has to wait.
      setLoading(false);
    });
    return unsubcribe;
    // return unsubcribe();
  }, []);

  return (
  <authContext.Provider value = {{ login, logout }}>{ children }</authContext.Provider>
  )}
