import React from "react";
import { collection, addDoc, Timestamp } from "firebase/firestore";
import { db } from "../../firebase/config.js";
import Swal from "sweetalert2";
import '../../stylesheets/WaiterView/Plates.css';

const userCollectionRef = collection(db, "orders");

const Footer = (props) => {
    const { cartItems, newCustomer, newTable} = props;

const createOrder = async () => {
    console.log("creado");
    await addDoc(userCollectionRef, {
      Customer: newCustomer,
      Table: newTable,
      Order: cartItems,
      status: "Pending",
      created: Timestamp.fromDate(new Date()),
    });
    console.log();
    Swal.fire({
      position: 'bottom-end',
      title: 'Orden enviada',
      icon: 'success',
      timer: 2000,
      timerProgressBar: true,
      showConfirmButton:false,
      toast: true,
    })
  };

<div className="btns_deleted_and_send_order">
<button id="btn_trash" className="btn"><i className="icon-trash"></i> Eliminar</button>
<button id="btn_send" className="btn" onClick={createOrder}><i className="icon-ok-sign"></i> Enviar</button>


</div>
}

export default Footer;
