import { useReducer } from 'react';
import TableRowSelect from './TableRowSelected';
import Plates from './Plates';
import TableHeader from './TableHeader';
import { shoppingInitialState, shoppingReducer} from './shoppingReducer'
import { TYPES } from './shoppingAction';

export const TableMain = () => {
    const [state,dispatch] = useReducer(shoppingReducer, shoppingInitialState);
    const {products, cart} = state;
    const addToCart = (id) => {
        //console.log(id)
        dispatch({type:TYPES.ADD_TO_CART, payload:id})
    };
    const delFromCart = (id, all = false) => {
        console.log(id, all)
        if(all){
            dispatch({type: TYPES.REMOVE_ALL_FROM_CART, payload:id})
        }else{
            dispatch({type: TYPES.REMOVE_ONE_FROM_CART, payload:id})
        }
    }
    const clearCart = () => {
        dispatch({type: TYPES.CLEAR_CART})
    }

    return (
        <div>
            <h2>PEDIDOS</h2>
            <h3>MESERO</h3>
            <div className='container-plates'> 
            {products.map((product) => (
            <Plates key={product.id} data={product} addToCart={addToCart} />
            ))}
            </div>
            <h3>carrito</h3>
            <div className='div_select_item_by_customer'>
            <table id="table_select_item" className="table table-striped">
            <TableHeader/>
             
            {/* <button >LIMPIAR CARRITO</button> */}
            {cart.map((item, index) => (
            <TableRowSelect key={index} data={item} delFromCart={delFromCart}/>
            
            ))}
            </table>
            </div>
            <div className="btns_deleted_and_send_order">   
            <button id="btn_trash" onClick={clearCart} className='btn'><i className="icon-trash"></i> Eliminar</button>
            <button id="btn_send" className='btn'><i className="icon-ok-sign"></i> Enviar</button>
            </div>
        </div>
    )
}
