import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import Menu from '../data/menu.json';

export function Breakfast() {
  return (
    <div className='container-breakfast'>
      <div className='d-grid gap-3 div_info_customer'>
      <h1 className='h1_title'>DESAYUNO</h1>
      <h2 className='h2_customer'>Cliente:</h2>
      <input type='text' className='form-control border-success input_name_customer'></input>
      <h2 className='h2_customer'>Mesa:</h2>
      <input type='number' className='form-control border-success input_name_customer'></input>
      </div>

      <div className="show_menues">
      {
        Menu && Menu.map(product => {
          return (
            <div className="container_products" key={product.id}>
              { product.nombre }
              { product.precio }
              
              
            
            <button className='btn_cafe btn-primary btn-l pill'>CAFÉ AMERICANO S/5.00</button>
            <button className='btn_cafe btn-primary btn-l pill'>CAFÉ CON LECHE S/7.00</button>
            <button className='btn_cafe btn-primary btn-l pill'>SANDWICH DE JAMÓN S/10.00</button>
            <button className='btn_cafe btn-primary btn-l pill'>{ product.categoria }<img className="img_product" src ={ product.img} /></button>

            </div>
          )
        })
      }
    </div>
    </div>
  )
}
